package ca.mcmaster.se2aa4.mazerunner;

import static org.junit.jupiter.api.Assertions.assertEquals;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

public class ConvertPathTest {
    private ConvertPath convertPath;

    
    @BeforeEach
    void setUp(){
        convertPath = new ConvertPath(new RightHandRule());
    }

    @Test
    void test_convertToFactorized_small(){ //test factorizing the small maze pathway
        StringBuilder pathway = new StringBuilder("FRFLLFFRFFRFFLLFFFFRFFRFFFFLLFFRFFFFRFFRFFLLFFLFFLFFFFRFFRFFLLFFFFRFFRFFLLFFRFFRFFFFRFFLFFRFFLF");
        StringBuilder output = convertPath.convertToFactorized(pathway);
        String expectedOutput = "FRF2L2FR2FR2F2L4FR2FR4F2L2FR4FR2FR2F2L2FL2FL4FR2FR2F2L4FR2FR2F2L2FR2FR4FR2FL2FR2FLF";
        assertEquals(expectedOutput, output.toString());
    }

    @Test
    void test_convertToFactorized_medium(){ //test factorizing the medium maze pathway
        StringBuilder pathway = new StringBuilder("");
        StringBuilder output = convertPath.convertToFactorized(pathway);
        String expectedOutput = "";
        assertEquals(expectedOutput, output.toString());
    }

    @Test
    void test_convertToFactorized_large(){ //test factorizing the large maze pathway
        StringBuilder pathway = new StringBuilder("");
        StringBuilder output = convertPath.convertToFactorized(pathway);
        String expectedOutput = "";
        assertEquals(expectedOutput, output.toString());
    }

    @Test
    void test_convertToStandard_small(){ // test converting the small maze found pathway to standard form
        String factorizedPathway = "FRF2L2FR2FR2F2L4FR2FR4F2L2FR4FR2FR2F2L2FL2FL4FR2FR2F2L4FR2FR2F2L2FR2FR4FR2FL2FR2FLF";
        convertPath.convertToStandard(factorizedPathway);
        String output = convertPath.getPathway();
        String expectedOutput = "FRFLLFFRFFRFFLLFFFFRFFRFFFFLLFFRFFFFRFFRFFLLFFLFFLFFFFRFFRFFLLFFFFRFFRFFLLFFRFFRFFFFRFFLFFRFFLF";
        assertEquals(expectedOutput, output);
    }

    @Test
    void test_convertToStandard_medium(){ // test converting the medium maze found pathway to standard form
        String factorizedPathway = "";
        convertPath.convertToStandard(factorizedPathway);
        String output = convertPath.getPathway();
        String expectedOutput = "";
        assertEquals(expectedOutput, output);
    }

    @Test
    void test_convertToStandard_large(){ // test converting the large maze found pathway to standard form
        String factorizedPathway = "";
        convertPath.convertToStandard(factorizedPathway);
        String output = convertPath.getPathway();
        String expectedOutput = "";
        assertEquals(expectedOutput, output);
    }
}
